import { Component } from 'react'
import { Radio } from 'antd';
import Utilities from '@/utilities';



class Add extends Component {

    constructor(props) {
        super(props)
        this.utility = new Utilities(this);
        this.columnSize = 12;
        this.state = {
            ...this.utility.getImageState,
            selected: {
                images: null, 
                category: null, //int
                city: null, //int
                region: null, //int
                village: null, //int
                metro: null, //int
                sellType: null, //int
                licence: null, //bool
                ipoteka: null, //bool
                roomAmount: null, //int
                apartmentFloor: null, //int
                buildingFloor: null, //int
                landAppointment: null, //int
                icareCixaris: null, //bool
                belediyye: null, //bool
                rentDuration: null, //int
                price: null, //int
                areaSize: null, //int
                aboutInfo: null, //string
                email: null, //string
                phone: null, //string
                name: null, //string
                wp: null, //bool
                call: null //bool
            },
            sellTypeList: null,
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
            metroList: null,
            landAppointmentList: null,
            rentDurationList: null,
            license: true,
            ipoteka: true,
            price: true,
            area: true,
            addition: {
                roomAmount: false,
                apartmantFloor: false,
                floorAmount: false,
                landAppointment: false,
                icare: false,
                belediyye: false,
            }
        }

        if (this.props.filter) {
            this.state.categoryList = this.props.filter.categories
            this.state.cityList = this.props.filter.cities
            this.state.sellTypeList = this.props.filter.sellTypes
            this.state.landAppointmentList = this.props.filter.landAppointment
            this.state.selected = {
                ...this.state.selected,
                sellType: this.props.filter.sellTypes[0].id
            }
        }
    }

    render(h) {
        return (
            <div id={'addSection'}>
                <div className={"container"} >
                    <div className={'row'} >
                        <div className={'item'} >

                            {this.utility.radioGenerator("Mən:", this.state.sellTypeList, 1, this.sellTypeCallback)}



                            {this.utility.selectGenerator("Kategoriya:", this.state.categoryList, "category", this.state.selected.category, this.categoryCallback, true, false)}


                            {this.utility.selectGenerator("Şəhər:", this.state.cityList, "city", this.state.selected.city, this.cityCallback, true, false)}



                            {this.utility.selectGenerator("Rayon:", this.state.regionList, "region", this.state.selected.region, this.regionCallback, true, false)}


                            {this.utility.selectGenerator("Qəsəbə:", this.state.villageList, "village", this.state.selected.village, this.commonCallback, true, false)}


                            {this.utility.selectGenerator("Metro:", this.state.metroList, "metro", this.state.selected.metro, this.commonCallback, true, false)}


                            {this.utility.selectGenerator("Kirayə müddəti:", this.state.rentDurationList, "rentDuration", this.state.selected.rentDuration, this.commonCallback, true, false)}


                            {this.utility.numberGenerator("Qiymət:", "Daxil Edin", this.numberCallback, "price", true)}


                            {this.utility.numberGenerator("Sahə:", "Daxil Edin", this.numberCallback, "areaSize", true)}



                            {this.utility.imageUploadGenerator(this.state.fileList, this.state.previewVisible, this.state.previewTitle, this.state.previewImage)}

                        </div>

                        <div className={'item'}>


                            {this.utility.numberGenerator("Otag:", "Daxil Edin", this.numberCallback, "roomAmount", this.state.addition.roomAmount)}


                            {this.utility.numberGenerator("Menzil Mertebesi:", "Daxil Edin", this.numberCallback, "apartmentFloor", this.state.apartmantFloor)}


                            {this.utility.numberGenerator("Bina Mertebesi:", "Daxil Edin", this.numberCallback, "buildingFloor", this.state.addition.floorAmount)}


                            {this.utility.selectGenerator("Torpag Teyinati:", this.state.addition.landAppointment ? this.state.landAppointmentList : false, "landAppointment", this.state.selected.landAppointment, this.commonCallback, null, true, false)}

                            {this.utility.checkBoxGenerator("Cixaris (Icare)", this.checkboxCallback, "icareCixaris")}
                            {this.utility.checkBoxGenerator("Belediyye", this.checkboxCallback, "belediyye")}
                            {this.utility.textAreaGeneretor("Məlumat:", "Ən azı 100 hərfdən ibarət olmalıdır", this.textAreaCallback, "aboutInfo")}
                            {this.utility.checkBoxGenerator("Cixaris", null)}
                            {this.utility.checkBoxGenerator("Ipoteka", null)}



                            {this.utility.inputGenerator("E-mail:", "Daxil Edin", this.commonCallback, "email")}


                            {this.utility.inputGenerator("Nömrə:", "Daxil Edin", this.commonCallback, "phone")}


                            {this.utility.inputGenerator("Ad:", "Daxil Edin", this.commonCallback, "name")}



                            {this.utility.checkBoxGenerator("WhatsApp", this.checkboxCallback, "wp")}
                            {this.utility.checkBoxGenerator("Zəng", this.checkboxCallback, "call")}

                        </div>



                        <div className={'item'} md={4}>Sider</div>
                    </div>
                </div>

            </div>
        )
    }

    sellTypeCallback = (value) => {
        let rent = this.state.sellTypeList.find(w => w.id == value.target.value).rent
        this.setState({
            selected: {
                ...this.state.selected,
                sellType: value.target.value,
                rentDuration: rent.length > 0 ? this.state.selected.rentDuration : null
            },
            rentDurationList: rent.length > 0 ? rent : null
        })
    }

    categoryCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                category: value
            },
            addition: {
                ...this.props.filter.categories.find(w => w.id == value)
            },
        })
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w => w.id == value).regions;
        let metros = this.state.cityList.find(w => w.id == value).metros;
        this.setState({
            selected: {
                ...this.state.selected,
                city: value,
                region: null,
                village: null,
                metro: null,
            },
            metroList: !metros.length ? null : metros,
            regionList: !regions.length ? null : regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        if (!value) {
            this.setState({
                selected: {
                    ...this.state.selected,
                    region: 0,
                    village: null,
                },
                villageList: null
            })

            return;
        }
        let villages = this.state.regionList.find(w => w.id == value).villages;
        this.setState({
            selected: {
                ...this.state.selected,
                region: value,
                village: null
            },
            villageList: !villages.length ? null : villages
        })
    }

    commonCallback = (value, option) => {
        this.setState({
            selected: {
                ...this.state.selected,
                [option.state]: value
            }
        })

    }

    textAreaCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                [value.target.name]: value.target.defaultValue
            }
        })
    }

    numberCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                [value.target.name]: +value.target.defaultValue
            }
        })
    }

    checkboxCallback = (e) => {
        let value = e.target.checked
        this.setState({
            selected: {
                ...this.state.selected,
                [e.target.state]: value
            }
        })
    }
}

export async function getStaticProps() {
    const res = await fetch('http://192.168.1.107:5566/api/bina/Form')
    const filter = await res.json()
    return {
        props: {
            filter,
        },
    }
}

export default Add;
