import { Component } from 'react'

import * as types from '../../store/types'
import { connect } from 'react-redux';
import Utilities from '@/utilities'


const mapStateToProps = (state) => {
    return {
        redux: state.redux
    };
}


class Filter extends Component {
    constructor(props) {
        super(props)
        this.utility = new Utilities()
        this.state = {
            selected: {
                category: null,
                city: null,
                region: null,
                village: null,
                metro: null,
                minPrice: null,
                maxPrice: null,
                minArea: null,
                maxArea: null,
                sellType: null,
                licence: null,
                ipoteka: null,
                minRoomAmount: null,
                maxRoomAmount: null,
                minApartmantFloor: null,
                maxApartmantFloor: null,
                minFloorAmount: null,
                maxFloorAmount: null,
            },
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
            metroList: null,
            price: true,
            area: true,
            addition: {
                roomAmount: false,
                apartmantFloor: false,
                floorAmount: false,
                landAppointment: false,
            }
        }

        if (props.filterJson.categories) {
            this.state.categoryList = props.filterJson.categories
        }
        if (props.filterJson.cities) {
            this.state.cityList = props.filterJson.cities
        }

    }




    componentDidUpdate = async (prevProps, prevState) => {
        console.log("Updated...")
        if (prevState.selected !== this.state.selected) {
            console.log(this.state.selected)
            let conn = await fetch("http://192.168.1.107:5566/api/bina/search", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                mode: 'cors',
                body: JSON.stringify(this.state.selected)
            });
            let res = await conn.json();

            this.props.dispatch({ type: types.SAVE_FILTER, payload: res })
        }

    }

    render(h) {

        return (
            <div className={'filter'}>
                <div className={'containerFilter'}>
                    {this.utility.selectGenerator("Kategoriya:", this.state.categoryList, "Category", this.state.selected.category, this.categoryCallback, false, false)}
                    {this.utility.selectGenerator("Şəhər:", this.state.cityList, "City", this.state.selected.city, this.cityCallback, true, false)}
                    {this.utility.selectGenerator("Rayon:", this.state.regionList, "Region", this.state.selected.region, this.regionCallback, true, true)}
                    {this.utility.selectGenerator("Qəsəbə:", this.state.villageList, "village", this.state.selected.village, this.commonCallback, true, true)}
                    {this.utility.selectGenerator("Metro:", this.state.metroList, "metro", this.state.selected.metro, this.commonCallback, true, true)}
                    {this.utility.priceİnputGenerator("Otaq Sayı:", "Min", "Max", 1, 1, this.minRoomAmountCallback, this.maxRoomAmountCallback, this.state.addition.roomAmount)}
                    {this.utility.priceİnputGenerator("Qiymət:", "Min", "Max", 1000, 1000, this.minPriceCallback, this.maxPriceCallback, this.state.price)}
                    {this.utility.priceİnputGenerator("Sahə:", "Min", "Max", 10, 1, this.minAreaCallback, this.maxAreaCallback, this.state.area)}
                    {this.utility.checkBoxGenerator("Çıxarış:", this.licenceCallback)}
                    {this.utility.checkBoxGenerator("Ipoteka:", this.ipotekaCallback)}
                    {
                        Object.values(this.state.addition).includes(true) ? this.utility.collapseAddition() : null
                    }
                </div>
            </div>
        )
    }

    categoryCallback = (value, options) => {
        let row = this.props.filterJson.categories.find(w => w.id == value);
        this.setState({
            selected: {
                ...this.state.selected,
                category: value
            },
            addition: {
                ...this.state.addition,
                roomAmount: row ? row.roomAmount : false,
                apartmantFloor: row ? row.floor : false,
                floorAmount: row ? row.floorAmount : false,
                landAppointment: row ? row.landAppointment : false,
            }
        })
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w => w.id == value).regions;
        let metros = this.state.cityList.find(w => w.id == value).metros;
        this.setState({
            selected: {
                ...this.state.selected,
                city: value,
                region: null,
                village: null,
                metro: null,
            },
            metroList: !metros.length ? null : metros,
            regionList: !regions.length ? null : regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        if (!value) {
            this.setState({
                selected: {
                    ...this.state.selected,
                    region: 0,
                    village: null,
                },
                villageList: null
            })

            return;
        }
        let villages = this.state.regionList.find(w => w.id == value).villages;
        this.setState({
            selected: {
                ...this.state.selected,
                region: value,
                village: null
            },
            villageList: !villages.length ? null : villages
        })
        console.log(value)
    }

    minPriceCallback = (value) => {
        console.log(value)
        this.setState({
            selected: {
                ...this.state.selected,
                minPrice: value
            }
        })
    }

    maxPriceCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                maxPrice: value
            }
        })
    }

    minAreaCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                maxArea: value
            }
        })
    }

    maxAreaCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                minArea: value
            }
        })
    }

    licenceCallback = (e) => {
        let value = e.target.checked
        this.setState({
            selected: {
                ...this.state.selected,
                licence: value
            }
        })
    }

    ipotekaCallback = (e) => {
        let value = e.target.checked
        this.setState({
            selected: {
                ...this.state.selected,
                ipoteka: value
            }
        })
    }

    minRoomAmountCallback = (value) => {
        if(typeof value !== 'number') return;
        this.setState({
            selected: {
                ...this.state.selected,
                minRoomAmount: value,
            }
        })
    }

    maxRoomAmountCallback = (value) => {
        if(typeof value !== 'number') return;
        this.setState({
            selected: {
                ...this.state.selected,
                maxRoomAmount: value
            }
        })
    }

    minApartmantFloorCallback = (value) => {
        if(typeof value !== 'number') return;
        this.setState({
            selected: {
                ...this.state.selected,
                minApartmantFloor: value
            }
        })
    }

    maxApartmantFloorCallback = (value) => {
        if(typeof value !== 'number') return;
        this.setState({
            ...this.state.selected,
            maxApartmantFloor: value
        })
    }

    minFloorAmountCallback = (value) => {
        if(typeof value !== 'number') return;
        this.setState({
            selected: {
                ...this.state.selected,
                minFloorAmount: value
            }
        })
    }

    maxFloorAmountCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                maxFloorAmount: value
            }
        })
    }

    commonCallback = (value, option) => {
        this.setState({
            selected: {
                ...this.state.selected,
                [option.state]: value
            }
        })

    }
}






export default connect(mapStateToProps)(Filter);
