import { Component } from 'react'
import { Select, Slider } from 'antd';

const { Option } = Select;

let selectGenerator = (title, options) => {
    return (<div className={'selectInput'}>
        <div className={'item'}><label>{title}</label></div>
        <div className={'item'}>
            <Select defaultValue="lucy" style={{ width: 120 }}>
                {options.map(w => <Option value={w.id}>{w.name}</Option>)}
            </Select>
        </div>
    </div>)
}


let sliderGenerator = (title, min, max) => {
    return (
        <div className='sliderInput'>
            <div className={'item'}>{title}</div>
            <div className={'item'}>
                <Slider
                    range
                    step={1000}
                    min={min}
                    max={max}
                    defaultValue={[min, max]}
                    marks={{0: `${min/1000} min M`, }}
                />
            </div>
        </div>
    )
}

let holder = [
    {
        id: 5,
        name: "Baki"
    },
    {
        id: 4,
        name: "Kelbecer"
    }
]

class Filter extends Component {
    render(h) {
        return (
            <div className={'filter'}>
                <div className={'container'}>
                    {selectGenerator("Şəhər:", holder)}
                    {sliderGenerator("Qiymet:", 20000, 500000)}
                </div>
            </div>
        )
    }

}


export default Filter;
