import { formatWithValidation } from 'next/dist/next-server/lib/utils';
import { Component } from 'react'
import { MdArrowBack, MdArrowForward } from "react-icons/md"
class Carousel extends Component {
    constructor(props) {
        super(props)

        this.state = {
            itemContainerWidth: 0
        }

        this.nextClick = this.nextClick.bind(this)
    }



    carouselPositionChangeClick(p) {
        if (typeof window !== 'undefined') {
            let items = document.querySelectorAll(".carousel-item-container");
            items.forEach((item)=>{
                item.style.transform = `translateX(${p=='left'?'-':''}${this.state.itemContainerWidth}px)`
            })
        }
    }

    componentDidMount() {
        this.setState({
            itemContainerWidth: document.querySelector('.carousel-item-container').clientWidth
        })
    }


    render(h) {
        return (
            <div className={'header-carousel'}>
                <div className={'previous-button'}>
                    <MdArrowBack />
                </div>
                <div className={'header-carousel-container'}>
                    {this.props.children}
                </div>
                <div className={'next-button'} onClick={()=>{this.nextClick('-')}}>
                    <MdArrowForward />
                </div>
            </div>

        );
    }

}

export default Carousel;
