import { Component } from 'react'
import { Radio } from 'antd';
import Utilities from '@/utilities';
import { Container, Row, Col } from 'react-bootstrap';


class Add extends Component {

    constructor(props) {
        super(props)
        this.utility = new Utilities(this);
        this.columnSize = 24;
        this.state = {
            ...this.utility.getImageState,
            selected: {
                images: null,
                category: null,
                city: null,
                region: null,
                village: null,
                metro: null,
                sellType: null,
                licence: null,
                ipoteka: null,
                roomAmount: null,
                apartmentFloor: null,
                buildingFloor: null,
                landAppointment: null,
                icareCixaris: null,
                belediyye: null,
                rentDuration: null,
                price: null,
                areaSize: null,
                aboutInfo: null,
                personalInformation: {
                    email: null,
                    phone: null,
                    name: null,
                    wp: null,
                    call: null
                }
            },
            sellTypeList: null,
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
            metroList: null,
            landAppointmentList: null,
            rentDurationList: null,
            license: true,
            ipoteka: true,
            price: true,
            area: true,
            addition: {
                roomAmount: false,
                apartmantFloor: false,
                floorAmount: false,
                landAppointment: false,
                icare: false,
                belediyye: false,
            }
        }

        if (this.props.filter) {
            this.state.categoryList = this.props.filter.categories
            this.state.cityList = this.props.filter.cities
        }
    }

    render(h) {
        return (
            <div id={'addSection'}>
                <Container fluid>
                    <Row md={12} noGutters={true}>
                        <Col md={4}>

                            <Col span={this.columnSize}>
                                <label>Mən:</label> <br />
                                <Radio.Group defaultValue="b" buttonStyle="solid">
                                    <Radio.Button value="b">Satıram</Radio.Button>
                                    <Radio.Button value="a">Kirayə Verirəm</Radio.Button>
                                </Radio.Group>
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.selectGenerator("Kategoriya:", this.state.categoryList, "category", this.state.selected.category, this.categoryCallback, true, false)}
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.selectGenerator("Şəhər:", this.state.cityList, "city", this.state.selected.city, this.cityCallback, true, false)}
                            </Col>

                            <Col span={this.columnSize}>
                                {this.utility.selectGenerator("Rayon:", this.state.regionList, "region", this.state.selected.region, this.regionCallback, true, false)}
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.selectGenerator("Qəsəbə:", this.state.villageList, "village", this.state.selected.village, this.commonCallback, true, false)}
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.selectGenerator("Metro:", this.state.metroList, "metro", this.state.selected.metro, this.commonCallback, true, false)}
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.selectGenerator("Kirayə müddəti:", [], "RentType", null, null, true, false)}
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.numberGenerator("Qiymət:", "Daxil Edin", null, [])}
                            </Col>
                            <Col span={this.columnSize}>
                                {this.utility.numberGenerator("Sahə:", "Daxil Edin", null, [])}
                            </Col>

                            <Row>
                                <Col span={this.columnSize}>{this.utility.imageUploadGenerator(this.state.fileList, this.state.previewVisible, this.state.previewTitle, this.state.previewImage)}</Col>
                            </Row>
                        </Col>

                        <Col md={4}>
                            <Col span={12}>
                                <Col span={this.columnSize}>
                                    {this.utility.numberGenerator("Otag:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={this.columnSize}>
                                    {this.utility.numberGenerator("Menzil Mertebesi:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={this.columnSize}>
                                    {this.utility.numberGenerator("Bina Mertebesi:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={this.columnSize}>
                                    {this.utility.selectGenerator("Torpag Teyinati:", [], "LandAppointment", null, null, true, false)}
                                </Col>
                                <Col span={this.columnSize}>{this.utility.checkBoxGenerator("Cixaris (Icare)", null)}</Col>
                                <Col span={this.columnSize}>{this.utility.checkBoxGenerator("Belediyye", null)}</Col>
                                <Col span={this.columnSize}>{this.utility.textAreaGeneretor("Məlumat", "Ən azı 100 hərfdən ibarət olmalıdır", null)}</Col>
                                <Col span={this.columnSize}>{this.utility.checkBoxGenerator("Cixaris", null)}</Col>
                                <Col span={this.columnSize}>{this.utility.checkBoxGenerator("Ipoteka", null)}</Col>
                            </Col>
                            <Row>
                                <Col span={this.columnSize}>
                                    {this.utility.inputGenerator("E-mail:", "Daxil Edin", null, null)}
                                </Col>
                                <Col span={this.columnSize}>
                                    {this.utility.inputGenerator("Nömrə:", "Daxil Edin", null, null)}
                                </Col>
                                <Col span={this.columnSize}>
                                    {this.utility.inputGenerator("Ad:", "Daxil Edin", null, null)}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={this.columnSize}>{this.utility.checkBoxGenerator("WhatsApp", null)}</Col>
                                <Col span={this.columnSize}>{this.utility.checkBoxGenerator("Zəng", null)}</Col>
                            </Row>
                        </Col>



                        <Col md={4}>Sider</Col>
                    </Row>
                </Container>

            </div>
        )
    }

    categoryCallback = (value, options) => {
        let row = this.props.filter.categories.find(w => w.id == value);
        this.setState({
            selected: {
                ...this.state.selected,
                category: value
            },
            addition: {
                ...this.state.addition,
                roomAmount: row ? row.roomAmount : false,
                apartmantFloor: row ? row.floor : false,
                floorAmount: row ? row.floorAmount : false,
                landAppointment: row ? row.landAppointment : false,
            }
        })
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w => w.id == value).regions;
        let metros = this.state.cityList.find(w => w.id == value).metros;
        this.setState({
            selected: {
                ...this.state.selected,
                city: value,
                region: null,
                village: null,
                metro: null,
            },
            metroList: !metros.length ? null : metros,
            regionList: !regions.length ? null : regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        if (!value) {
            this.setState({
                selected: {
                    ...this.state.selected,
                    region: 0,
                    village: null,
                },
                villageList: null
            })

            return;
        }
        let villages = this.state.regionList.find(w => w.id == value).villages;
        this.setState({
            selected: {
                ...this.state.selected,
                region: value,
                village: null
            },
            villageList: !villages.length ? null : villages
        })
    }

    commonCallback = (value, option) => {
        this.setState({
            selected: {
                ...this.state.selected,
                [option.state]: value
            }
        })

    }
}

export async function getStaticProps() {
    const res = await fetch('http://192.168.1.107:5566/api/bina/add')
    const filter = await res.json()
    return {
        props: {
            filter,
        },
    }
}

export default Add;
