import { Component } from 'react'
import * as types from '../../store/types'
import { connect } from 'react-redux';
import Utilities from '@/utilities'

const mapStateToProps = (state) => {
    return {
        redux: state.redux
    };
}


class Filter extends Component {
    constructor(props) {
        super(props)
        this.utility = new Utilities(this)
        this.state = {
            selected: {
                category: null,
                city: null,
                region: null,
                village: null,
                metro: null,
                minPrice: null,
                maxPrice: null,
                minArea: null,
                maxArea: null,
                sellType: null,
                licence: null,
                ipoteka: null,
                minRoomAmount: null,
                maxRoomAmount: null,
                minApartmantFloor: null,
                maxApartmantFloor: null,
                minFloorAmount: null,
                maxFloorAmount: null,
            },
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
            metroList: null,
            price: true,
            area: true,
            addition: {
                roomAmount: false,
                apartmantFloor: false,
                floorAmount: false,
                landAppointment: false,
            }
        }

        if (props.filterJson.categories) {
            this.state.categoryList = props.filterJson.categories
        }
        if (props.filterJson.cities) {
            this.state.cityList = props.filterJson.cities
        }

    }

    componentDidUpdate = async (prevProps, prevState) => {
        if (prevState.selected !== this.state.selected) {
            console.log(this.state.selected)
            let conn = await fetch("http://192.168.1.107:5566/api/bina/search", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                mode: 'cors',
                body: JSON.stringify(this.state.selected)
            });
            let res = await conn.json();

            this.props.dispatch({ type: types.SAVE_FILTER, payload: res })
        }

    }

    render(h) {

        return (
            <div className={'filter'}>
                <div className={'containerFilter'}>
                    {this.utility.selectGenerator("Kategoriya:", this.state.categoryList, "Category", this.state.selected.category, this.categoryCallback, false, false)}
                    {this.utility.selectGenerator("Şəhər:", this.state.cityList, "City", this.state.selected.city, this.cityCallback, true, false)}
                    {this.utility.selectGenerator("Rayon:", this.state.regionList, "Region", this.state.selected.region, this.regionCallback, true, true)}
                    {this.utility.selectGenerator("Qəsəbə:", this.state.villageList, "village", this.state.selected.village, this.commonCallback, true, true)}
                    {this.utility.selectGenerator("Metro:", this.state.metroList, "metro", this.state.selected.metro, this.commonCallback, true, true)}
                    {this.utility.priceİnputGenerator("Otaq Sayı:", "minRoomAmount", "maxRoomAmount", 1, 1, this.minRoomAmountCallback, this.maxRoomAmountCallback, this.state.addition.roomAmount)}
                    {this.utility.priceİnputGenerator("Qiymət:", "minPrice", "maxPrice", 1000, 1000, this.numberCommonCallback, this.state.price)}
                    {this.utility.priceİnputGenerator("Sahə:", "minArea", "maxArea", 10, 1, this.numberCommonCallback, this.state.area)}
                    {this.utility.checkBoxGenerator("Çıxarış:", this.checkboxCallback, "licence")}
                    {this.utility.checkBoxGenerator("Ipoteka:", this.checkboxCallback, "ipoteka")}
                    {
                        Object.values(this.state.addition).includes(true) ? this.utility.collapseAddition() : null
                    }
                </div>
            </div>
        )
    }

    categoryCallback = (value, options) => {
        let row = this.props.filterJson.categories.find(w => w.id == value);
        this.setState({
            selected: {
                ...this.state.selected,
                category: value
            },
            addition: {
                ...this.state.addition,
                roomAmount: row ? row.roomAmount : false,
                apartmantFloor: row ? row.floor : false,
                buildingFloor: row ? row.buildingFloor : false,
                landAppointment: row ? row.landAppointment : false,
            }
        })
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w => w.id == value).regions;
        let metros = this.state.cityList.find(w => w.id == value).metros;
        this.setState({
            selected: {
                ...this.state.selected,
                city: value,
                region: null,
                village: null,
                metro: null,
            },
            metroList: !metros.length ? null : metros,
            regionList: !regions.length ? null : regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        if (!value) {
            this.setState({
                selected: {
                    ...this.state.selected,
                    region: 0,
                    village: null,
                },
                villageList: null
            })

            return;
        }
        let villages = this.state.regionList.find(w => w.id == value).villages;
        this.setState({
            selected: {
                ...this.state.selected,
                region: value,
                village: null
            },
            villageList: !villages.length ? null : villages
        })
    }

    checkboxCallback = (e) => {
        let value = e.target.checked
        this.setState({
            selected: {
                ...this.state.selected,
                [e.target.state]: value
            }
        })
    }

    numberCommonCallback = (e) => {
        if(typeof parseInt(e.target.ariaValueNow) !== 'number') return;
        this.setState({
            selected: {
                ...this.state.selected,
                [e.target.name]: parseInt(e.target.ariaValueNow)
            }
        })
    }

    commonCallback = (value, option) => {
        this.setState({
            selected: {
                ...this.state.selected,
                [option.state]: value
            }
        })

    }
}






export default connect(mapStateToProps)(Filter);
