import { Select, Checkbox, InputNumber, Collapse, Input } from 'antd';
const { Option } = Select;
const { Panel } = Collapse;
const { TextArea } = Input;

class Utilities {

    inputGenerator = (title, placeholder, callback, input) => {
        return (
            <div className={'simpleInput'}>
                <lable>{title}</lable>
                <Input placeholder={placeholder} value={input} onChange={callback} />
            </div>
        )
    }

    numberGenerator = (title, placeholder, callback, visibility) => {
        if (!visibility) return null;
        return (
            <div className={'numberInput'}>
                <label>{title}</label>
                <InputNumber
                    placeholder={placeholder}
                    formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                    parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    onChange={callback}
                />
            </div>
        )
    }

    textAreaGeneretor = (title, placeholder, callback) => {
        return (
            <div className={'textAreaInput'}>
                <label>{title}</label>
                <TextArea
                    placeholder={placeholder}
                    autoSize={{ minRows: 2, maxRows: 12 }}
                    onChange={callback}
                />
            </div>
        )
    }

    priceİnputGenerator = (title, minName, maxName, min, step, minCallback, maxCallback, visibility) => {
        if (!visibility) return null
        return (
            <div className={'priceInput'}>
                <div className={'item'}><label>{title}</label></div>
                <div className={'item'}>
                    <InputNumber
                        placeholder={"Min."}
                        onChange={minCallback}
                        min={min} step={step} name={minName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value+min}
                    />
                -
                <InputNumber
                        placeholder={"Max."}
                        onChange={maxCallback}
                        min={min} step={step} name={maxName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                </div>
            </div>
        )
    }

    selectGenerator = (title, options, name, selected, callback, sort, selectAll) => {
        if (!options) return null
        if (sort) {
            options.sort((a, b) => {
                let fa = a.name.toLowerCase()
                let fb = b.name.toLowerCase()

                if (fa < fb) return -1
                if (fa > fb) return 1
                return 0
            })
        }
        return (<div className={'selectInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select onSelect={callback} placeholder={"Seçin"} value={selected} name={name} style={{ width: 120 }}>
                    {selectAll ? <Option state={name} value={0}>Hamısı</Option> : null}
                    {options.map((w, i) => <Option state={name} key={new Date().getTime() + i} value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    checkBoxGenerator = (title, callback) => {
        return (
            <div className={'checkInput'}>
                <Checkbox onChange={callback}>{title}</Checkbox>
            </div>
        )
    }

    collapseAddition = () => {
        return (
           /* <Collapse>
                <Panel header="Əlavə" key="1">
                    {this.priceİnputGenerator("Mənzilin Mərtəbəsi:", "Min", "Max", 1, 1, this.minApartmantFloorCallback, this.maxApartmantFloorCallback, this.state.addition.apartmantFloor)}
                    {this.priceİnputGenerator("Binanın Mərtəbə Sayı:", "Min", "Max", 1, 1, this.minFloorAmountCallback, this.maxFloorAmountCallback, this.state.addition.floorAmount)}
                </Panel>
            </Collapse>*/ null
        )
    }

}

export default Utilities;
