import { Component } from 'react'
import { Select, Slider, Checkbox, InputNumber } from 'antd';

const { Option } = Select;


let priceİnputGenerator = (title, minName, maxName, min, step) => {
    return (
        <div className={'priceInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <InputNumber
                    placeholder={"Min."}
                    min={min}
                    step={step}
                    name={minName}
                    formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                    parser={value => value.replace(/\$\s?|(,*)/g, '')}
                />
                -
                <InputNumber
                    placeholder={"Max."}
                    min={min}
                    step={step}
                    name={maxName}
                    formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                    parser={value => value.replace(/\$\s?|(,*)/g, '')}
                />
            </div>
        </div>

    )
}

let selectGenerator = (title, options, name) => {
    return (<div key={(Math.floor(Math.random()*100)-1)} className={'selectInput'}>
        <div className={'item'}><label>{title}</label></div>
        <div className={'item'}>
            <Select name={name} defaultValue="lucy" style={{ width: 120 }}>
                {options.map(w => <Option value={w.id}>{w.name}</Option>)}
            </Select>
        </div>
    </div>)
}

let checkBoxGenerator = (title) => {
    return (
        <div className={'checkInput'}>
            <Checkbox>{title}</Checkbox>
        </div>
    )
}

let sliderGenerator = (title, min, max) => {
    let marks = {
        [min]: { style: { width: '100%', left: '8%' }, label: `${min / 1000}.000 M` },
        [max]: { style: { width: '100%', left: '92%' }, label: `${max / 1000}.000 M` }
    }
    return (
        <div className='sliderInput'>
            <div className={'item'}>{title}</div>
            <div className={'item'}>
                <Slider
                    range
                    step={5000}
                    min={min}
                    max={max}
                    defaultValue={[min, max]}
                    tipFormatter={(value) => <div>{value / 1000}.000</div>}
                    marks={marks}
                />
            </div>
        </div>
    )
}

let holder = [
    {
        key: (Math.floor(Math.random()*100)-1),
        id: 5,
        name: "Baki"
    },
    {
        key: (Math.floor(Math.random()*100)-1),
        id: 4,
        name: "Kelbecer"
    }
]

class Filter extends Component {
    constructor(props){
        super(props)
        console.log(this.props)
    }
    render(h) {
       
        return (
            <div className={'filter'}>
                <div className={'containerFilter'}>
                    {selectGenerator("Kategoriya:", holder, "Category")}
                    {selectGenerator("Şəhər:", holder, "City")}
                    {selectGenerator("Rayon:", holder, "Region")}
                    {selectGenerator("Qəsəbə:", holder, "Village")}
                    {selectGenerator("Metro:", holder, "Metro")}
                    {priceİnputGenerator("Qiymət:", "minPrice", "maxPrice", 1000, 1000)}
                    {priceİnputGenerator("Sahə:", "minArea", "maxArea", 10, 1)}
                    {checkBoxGenerator("Çıxarış")}
                    {checkBoxGenerator("Ipoteka")}
                </div>
            </div>
        )
    }

}


export async function getServerSideProps(context) {
    // Call an external API endpoint to get posts.
    // You can use any data fetching library
    const res = await fetch('https://localhost:44344/api/bina/filter')
    const bina_filter = await res.json()
  

    return {
      props: {
        bina_filter,
      },
    }
  }


export default Filter;
