import { Component } from 'react'
import { Select, Slider, Checkbox, InputNumber, Collapse } from 'antd';

const { Option } = Select;
const { Panel } = Collapse;

class Filter extends Component {
    constructor(props) {
        super(props)

        this.state = {
            selected: {
                category: null,
                city: null,
                region: null,
                village: null,
                metro: null,
                minPrice: null,
                maxPrice: null,
                minArea: null,
                maxArea: null,
                sellType: null,
                licence: false,
                ipoteka: false,
                minRoomAmount: null,
                maxRoomAmount: null,
                minApartmantFloor: null,
                maxApartmantFloor: null,
                minFloorAmount: null,
                maxFloorAmount: null,
            },
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
            metroList: null,
            price: true,
            area: true,
            addition: {
                roomAmount: false,
                apartmantFloor: false,
                floorAmount: false,
                landAppointment: false,
            }
        }

        if (props.filterJson.categories) {
            this.state.categoryList = props.filterJson.categories
        }
        if (props.filterJson.cities) {
            this.state.cityList = props.filterJson.cities
        }

    }

    priceİnputGenerator = (title, minName, maxName, min, step, minCallback, maxCallback, visibility) => {
        if (!visibility) return null
        return (
            <div className={'priceInput'}>
                <div className={'item'}><label>{title}</label></div>
                <div className={'item'}>
                    <InputNumber
                        placeholder={"Min."}
                        onChange={minCallback}
                        min={min} step={step} name={minName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                -
                <InputNumber
                        placeholder={"Max."}
                        onChange={maxCallback}
                        min={min} step={step} name={maxName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                </div>
            </div>
        )
    }


    sliderGenerator = (title, min, max, callback) => {
        let marks = {
            [min]: { style: { width: '100%', left: '8%' }, label: `${min}` },
            [max]: { style: { width: '100%', left: '92%' }, label: `${max}+` }
        }
        return (
            <div className='sliderInput'>
                <div className={'item'}>{title}</div>
                <div className={'item'}>
                    <Slider
                        range
                        step={1}
                        min={min}
                        max={max}
                        onChange={callback}
                        defaultValue={[min, max]}
                        tipFormatter={(value) => <div>{value}</div>}
                        marks={marks}
                    />
                </div>
            </div>
        )
    }

    selectGenerator = (title, options, name, selected, callback, sort, selectAll, objName) => {
        if (!options) return null
        if (sort) {
            options.sort((a, b) => {
                let fa = a.name.toLowerCase()
                let fb = b.name.toLowerCase()

                if (fa < fb) return -1
                if (fa > fb) return 1
                return 0
            })
        }
        return (<div className={'selectInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select onSelect={callback} placeholder={"Seçin"} value={selected} name={name} style={{ width: 120 }}>
                    {selectAll ? <Option state={objName} value={0}>Hamısı</Option> : null}
                    {options.map((w, i) => <Option state={objName} key={new Date().getTime() + i} value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    checkBoxGenerator = (title, callback) => {
        return (
            <div className={'checkInput'}>
                <Checkbox onChange={callback}>{title}</Checkbox>
            </div>
        )
    }

    collapseAddition = () => {
        return (
            <Collapse>
                <Panel header="Əlavə" key="1">
                    {this.priceİnputGenerator("Mənzilin Mərtəbəsi:", "Min", "Max", 1, 1, this.minApartmantFloorCallback, this.maxApartmantFloorCallback, this.state.addition.apartmantFloor)}
                    {this.priceİnputGenerator("Binanın Mərtəbə Sayı:", "Min", "Max", 1, 1, this.minFloorAmountCallback, this.maxFloorAmountCallback, this.state.addition.floorAmount)}
                </Panel>
            </Collapse>
        )
    }

    componentDidUpdate = async ()=> {
        let header = {
            method: "POST",
            headers: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify(this.state.selected)
        }
        let conn = await fetch("http://192.168.1.107:5566/api/bina/search", header);
        let res = await conn.json();
        
        console.log(res)
    }

    render(h) {

        return (
            <div className={'filter'}>
                <div className={'containerFilter'}>
                    {this.selectGenerator("Kategoriya:", this.state.categoryList, "Category", this.state.selected.category, this.categoryCallback, false, false, null)}
                    {this.selectGenerator("Şəhər:", this.state.cityList, "City", this.state.selected.city, this.cityCallback, true, false)}
                    {this.selectGenerator("Rayon:", this.state.regionList, "Region", this.state.selected.region, this.regionCallback, true, true)}
                    {this.selectGenerator("Qəsəbə:", this.state.villageList, "Village", this.state.selected.village, this.commonCallback, true, true, "village")}
                    {this.selectGenerator("Metro:", this.state.metroList, "Metro", this.state.selected.metro, this.commonCallback, true, true, "metro")}
                    {this.priceİnputGenerator("Otaq Sayı:", "Min", "Max", 1, 1, this.minRoomAmountCallback, this.maxRoomAmountCallback, this.state.addition.roomAmount)}
                    {this.priceİnputGenerator("Qiymət:", "Min", "Max", 1000, 1000, this.minPriceCallback, this.maxPriceCallback, this.state.price)}
                    {this.priceİnputGenerator("Sahə:", "Min", "Max", 10, 1, this.minAreaCallback, this.maxAreaCallback, this.state.area)}
                    {this.checkBoxGenerator("Çıxarış:", this.licenceCallback)}
                    {this.checkBoxGenerator("Ipoteka:", this.ipotekaCallback)}
                    {
                        Object.values(this.state.addition).includes(true) ? this.collapseAddition() : null
                    }
                </div>
            </div>
        )
    }

    categoryCallback = (value, options) => {
        let row = this.props.filterJson.categories.find(w => w.id == value);
        this.setState({
            selected: {
                ...this.state.selected,
                category: value
            },
            addition: {
                ...this.state.addition,
                roomAmount: row ? row.roomAmount : false,
                apartmantFloor: row ? row.floor : false,
                floorAmount: row ? row.floorAmount : false,
                landAppointment: row ? row.landAppointment : false,
            }
        })
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w => w.id == value).regions;
        let metros = this.state.cityList.find(w => w.id == value).metros;

        this.setState({
            selected: {
                ...this.state.selected,
                city: value,
                region: null,
                village: null,
                metro: null,
            },
            metroList: !metros.length ? null : metros,
            regionList: !regions.length ? null : regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        if (!value) {
            this.setState({
                selected: {
                    ...this.state.selected,
                    region: 0,
                    village: null,
                },
                villageList: null
            })

            return;
        }
        let villages = this.state.regionList.find(w => w.id == value).villages;
        this.setState({
            selected: {
                ...this.state.selected,
                region: value,
                village: null
            },
            villageList: !villages.length ? null : villages
        })
    }

    minPriceCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                minPrice: value
            }
        })
    }

    maxPriceCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                maxPrice: value
            }
        })
    }

    minAreaCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                maxArea: value
            }
        })
    }

    maxAreaCallback = (value) => {
        this.setState({
            selected: {
                ...this.state.selected,
                minArea: value
            }
