import { Component } from 'react'
import { Row, Col, Radio, Select, Layout } from 'antd';
import Utilities from '@/utilities';

const { Option } = Select;
const { Header, Footer, Sider, Content } = Layout;

class Add extends Component {

    constructor(props) {
        super(props)
        this.utility = new Utilities();
    }

    priceİnputGenerator = (title, minName, maxName, min, step, minCallback, maxCallback, visibility) => {
        if (!visibility) return null
        return (
            <div className={'priceInput'}>
                <div className={'item'}><label>{title}</label></div>
                <div className={'item'}>
                    <InputNumber
                        placeholder={"Min."}
                        onChange={minCallback}
                        min={min} step={step} name={minName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                -
                <InputNumber
                        placeholder={"Max."}
                        onChange={maxCallback}
                        min={min} step={step} name={maxName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                </div>
            </div>
        )
    }

    selectGenerator = (title, options, name, selected, callback, sort, selectAll) => {
        if (!options) return null
        if (sort) {
            options.sort((a, b) => {
                let fa = a.name.toLowerCase()
                let fb = b.name.toLowerCase()

                if (fa < fb) return -1
                if (fa > fb) return 1
                return 0
            })
        }
        return (<div className={'selectInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select onSelect={callback} placeholder={"Seçin"} value={selected} name={name} style={{ width: 120 }}>
                    {selectAll ? <Option state={name} value={0}>Hamısı</Option> : null}
                    {options.map((w, i) => <Option state={name} key={new Date().getTime() + i} value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    render(h) {
        return (
            <div id={'addSection'}>
                <Layout>
                    <Layout>
                        <Content>
                            <Row>
                                <Col span={8}>
                                    <label>Mən:</label> <br />
                                    <Radio.Group defaultValue="b" buttonStyle="solid">
                                        <Radio.Button value="b">Satıram</Radio.Button>
                                        <Radio.Button value="a">Kirayə Verirəm</Radio.Button>
                                    </Radio.Group>
                                </Col>
                                <Col span={8}>
                                    {this.selectGenerator("Kategoriya:", [], "Category", null, null, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.selectGenerator("Şəhər:", [], "City", null, null, true, false)}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={8}>
                                    {this.selectGenerator("Rayon:", [], "Region", null, null, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.selectGenerator("Qəsəbə:", [], "Village", null, null, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.selectGenerator("Metro:", [], "Metro", null, null, true, false)}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={8}>
                                    {this.selectGenerator("Kirayə müddəti:",[], "RentType",null,null,true,false)}
                                </Col>
                                <Col span={8}>
                                    
                                </Col>
                                <Col span={8}></Col>
                            </Row>
                        </Content>
                    </Layout>
                    <Sider width={'25%'}>Sider</Sider>
                </Layout>

            </div>
        )
    }
}

export default Add;
