import { Component } from 'react'
import { Select, Slider, Checkbox, InputNumber } from 'antd';

const { Option } = Select;



let holder = [
    {
        id: 5,
        name: "Baki"
    },
    {
        id: 4,
        name: "Kelbecer"
    }
]

class Filter extends Component {
    constructor(props) {
        super(props)

        this.state = {
            selected: {
                category: null,
                city: null,
                region: null,
                village: null,
            },
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
        }

        if (props.filterJson.categories) {
            this.state.categoryList = props.filterJson.categories
        }
        if (props.filterJson.cities) {
            this.state.cityList = props.filterJson.cities
        }
        

        console.log(props.filterJson)

        /*{selectGenerator("Kategoriya:", holder, "Category")}
        this.filterList.push({key: this.index++, input: this.cityInput})
                    {selectGenerator("Şəhər:", holder, "City")}
                    {selectGenerator("Rayon:", holder, "Region")}
                    {selectGenerator("Qəsəbə:", holder, "Village")}
                    {selectGenerator("Metro:", holder, "Metro")}
                    {priceİnputGenerator("Qiymət:", "minPrice", "maxPrice", 1000, 1000)}
                    {priceİnputGenerator("Sahə:", "minArea", "maxArea", 10, 1)}
                    {checkBoxGenerator("Çıxarış")}
                    {checkBoxGenerator("Ipoteka")}*/
    }



    priceİnputGenerator = (title, minName, maxName, min, step) => {
        return (
            <div className={'priceInput'}>
                <div className={'item'}><label>{title}</label></div>
                <div className={'item'}>
                    <InputNumber
                        placeholder={"Min."}
                        min={min}
                        step={step}
                        name={minName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                -
                <InputNumber
                        placeholder={"Max."}
                        min={min}
                        step={step}
                        name={maxName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                </div>
            </div>

        )
    }

    selectGenerator = (title, options, name, selected, callback) => {
        if (!options) return null

        return (<div className={'selectInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select onSelect={callback} value={selected} name={name} style={{ width: 120 }}>
                    {options.map((w, i) => <Option value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    checkBoxGenerator = (title) => {
        return (
            <div className={'checkInput'}>
                <Checkbox>{title}</Checkbox>
            </div>
        )
    }

    sliderGenerator = (title, min, max) => {
        let marks = {
            [min]: { style: { width: '100%', left: '8%' }, label: `${min / 1000}.000 M` },
            [max]: { style: { width: '100%', left: '92%' }, label: `${max / 1000}.000 M` }
        }
        return (
            <div className='sliderInput'>
                <div className={'item'}>{title}</div>
                <div className={'item'}>
                    <Slider
                        range
                        step={5000}
                        min={min}
                        max={max}
                        defaultValue={[min, max]}
                        tipFormatter={(value) => <div>{value / 1000}.000</div>}
                        marks={marks}
                    />
                </div>
            </div>
        )
    }

    render(h) {

        return (
            <div className={'filter'}>
                <div className={'containerFilter'}>
                    {this.selectGenerator("Kategoriya:", this.state.categoryList, "Category")}
                    {this.selectGenerator("Şəhər:", this.state.cityList, "City", this.state.selected.city, this.cityCallback)}
                    {this.selectGenerator("Rayon:", this.state.regionList, "Region", this.state.selected.region, this.regionCallback)}
                    {this.selectGenerator("Qəsəbə:", this.state.villageList, "Village", this.state.selected.village, null)}
                </div>
            </div>
        )
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w=> w.id == value).regions;
        this.setState({
            selected:{
                ...this.state.selected,
                city: value
            },
            regionList: !regions.length?null:regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        let villages = this.state.regionList.find(w=>w.id == value).villages;
        this.setState({
            selected:{
                ...this.state.selected,
                region: value,
                village: villages[0].id
            },
            villageList: !villages.length?null:villages
        })
    }

    villageCallback = (value) =>{
        this.setState({
            sele
        })
    }

}





export default Filter;
