import { Component } from 'react'
import { Row, Col, Radio, Select, Layout } from 'antd';
import Utilities from '@/utilities';

const { Sider, Content } = Layout;

class Add extends Component {

    constructor(props) {
        super(props)
        this.utility = new Utilities(this);

        this.state = {
            ...this.utility.getImageState,
            selected: {
                images: null,
                category: null,
                city: null,
                region: null,
                village: null,
                metro: null,
                sellType: null,
                licence: null,
                ipoteka: null,
                roomAmount: null,
                apartmentFloor: null,
                buildingFloor: null,
                landAppointment: null,
                icareCixaris: null,
                belediyye: null,
                rentDuration: null,
                price: null,
                areaSize: null,
                aboutInfo: null,
                personalInformation: {
                    email: null,
                    phone: null,
                    name: null,
                    wp: null,
                    call: null
                }
            },
            sellTypeList: null,
            categoryList: null,
            cityList: null,
            regionList: null,
            villageList: null,
            metroList: null,
            landAppointmentList: null,
            rentDurationList: null,
            license: true,
            ipoteka: true,
            price: true,
            area: true,
            addition: {
                roomAmount: false,
                apartmantFloor: false,
                floorAmount: false,
                landAppointment: false,
                icare: false,
                belediyye: false,
            }
        }

        if(this.props.filter){
            this.state.categoryList = this.props.filter.categories
        }
    }

    render(h) {
        return (
            <div id={'addSection'}>
                <Layout>
                    <Layout>
                        <Content>
                            <Row>
                                <Col span={8}>
                                    <label>Mən:</label> <br />
                                    <Radio.Group defaultValue="b" buttonStyle="solid">
                                        <Radio.Button value="b">Satıram</Radio.Button>
                                        <Radio.Button value="a">Kirayə Verirəm</Radio.Button>
                                    </Radio.Group>
                                </Col>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Kategoriya:", this.state.categoryList, "category", this.state.selected.category, this.categoryCallback, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Şəhər:", this.state.cityList, "city", null, null, true, false)}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Rayon:", [], "Region", null, null, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Qəsəbə:", [], "Village", null, null, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Metro:", [], "Metro", null, null, true, false)}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={8}>
                                    {this.utility.numberGenerator("Otag:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={8}>
                                    {this.utility.numberGenerator("Menzil Mertebesi:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={8}>
                                    {this.utility.numberGenerator("Bina Mertebesi:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Torpag Teyinati:", [], "LandAppointment", null, null, true, false)}
                                </Col>
                                <Col span={8}>{this.utility.checkBoxGenerator("Cixaris (Icare)", null)}</Col>
                                <Col span={8}>{this.utility.checkBoxGenerator("Belediyye", null)}</Col>
                            </Row>
                            <Row>
                                <Col span={8}>
                                    {this.utility.selectGenerator("Kirayə müddəti:", [], "RentType", null, null, true, false)}
                                </Col>
                                <Col span={8}>
                                    {this.utility.numberGenerator("Qiymət:", "Daxil Edin", null, [])}
                                </Col>
                                <Col span={8}>
                                    {this.utility.numberGenerator("Sahə:", "Daxil Edin", null, [])}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={8}>{this.utility.textAreaGeneretor("Məlumat", "Ən azı 100 hərfdən ibarət olmalıdır", null)}</Col>
                                <Col span={8}>{this.utility.checkBoxGenerator("Cixaris", null)}</Col>
                                <Col span={8}>{this.utility.checkBoxGenerator("Ipoteka", null)}</Col>
                            </Row>
                            <Row>
                                <Col span={8}>
                                    {this.utility.inputGenerator("E-mail:", "Daxil Edin", null, null)}
                                </Col>
                                <Col span={8}>
                                    {this.utility.inputGenerator("Nömrə:", "Daxil Edin", null, null)}
                                </Col>
                                <Col span={8}>
                                    {this.utility.inputGenerator("Ad:", "Daxil Edin", null, null)}
                                </Col>
                            </Row>
                            <Row>
                                <Col span={8}>{this.utility.checkBoxGenerator("WhatsApp", null)}</Col>
                                <Col span={8}>{this.utility.checkBoxGenerator("Zəng", null)}</Col>
                            </Row>
                            <Row>
                                <Col span={8}>{this.utility.imageUploadGenerator(this.state.fileList, this.state.previewVisible, this.state.previewTitle, this.state.previewImage)}</Col>
                            </Row>
                        </Content>
                    </Layout>
                    <Sider width={'25%'}>Sider</Sider>
                </Layout>

            </div>
        )
    }

    categoryCallback = (value, options) => {
        let row = this.props.filter.categories.find(w => w.id == value);
        this.setState({
            selected: {
                ...this.state.selected,
                category: value
            },
            addition: {
                ...this.state.addition,
                roomAmount: row ? row.roomAmount : false,
                apartmantFloor: row ? row.floor : false,
                floorAmount: row ? row.floorAmount : false,
                landAppointment: row ? row.landAppointment : false,
            }
        })
    }

    cityCallback = (value) => {
        let regions = this.state.cityList.find(w => w.id == value).regions;
        let metros = this.state.cityList.find(w => w.id == value).metros;
        this.setState({
            selected: {
                ...this.state.selected,
                city: value,
                region: null,
                village: null,
                metro: null,
            },
            metroList: !metros.length ? null : metros,
            regionList: !regions.length ? null : regions,
            villageList: null
        })
    }

    regionCallback = (value) => {
        if (!value) {
            this.setState({
                selected: {
                    ...this.state.selected,
                    region: 0,
                    village: null,
                },
                villageList: null
            })

            return;
        }
        let villages = this.state.regionList.find(w => w.id == value).villages;
        this.setState({
            selected: {
                ...this.state.selected,
                region: value,
                village: null
            },
            villageList: !villages.length ? null : villages
        })
    }
}

export async function getStaticProps() {
    const res = await fetch('http://192.168.1.107:5566/api/bina/add')
    const filter = await res.json()
    return {
        props: {
            filter,
        },
    }
}

export default Add;
