import { Component } from 'react'
import Filter from '@/subcomponents/filter'
import { Save_Filter_Json } from 'redux/actions/filterAction';


class Search extends Component {
    constructor(props){
        super(props)
        this.saveFilter()
    }

    getini

    saveFilter = async (e)=>{
        this.props.dispatch({type: Save_Filter_Json, payload: this.props.filter})
    }

    render(h) {
        console.log(this.props.filterJsons)
        return (
            <div className={'container'}>
                <div className={'leftSide'}>
                    <Filter redux={{fun: this.props.dispatch}} filterJson={this.props.filter}></Filter>
                </div>
                <div className={'rightSide'}></div>
            </div>
        );
    }
}

Search.getInitialProps = ({pathname, query}) => ({        
    custom: 'custom' // pass some custom props to component
  });

export async function getStaticProps() {
    const res = await fetch('http://192.168.1.107:5566/api/bina/filter')
    const filter = await res.json()
    return {
      props: {
        filter,
      },
    }
}

export default Search;
