import { Component } from 'react'
import { Row, Col, Radio } from 'antd';


class Add extends Component {

    constructor(props) {
        super(props)

    }

    selectGenerator = (title, options, name, selected, callback, sort, selectAll) => {
        if (!options) return null
        if (sort) {
            options.sort((a, b) => {
                let fa = a.name.toLowerCase()
                let fb = b.name.toLowerCase()

                if (fa < fb) return -1
                if (fa > fb) return 1
                return 0
            })
        }
        return (<div className={'selectInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select onSelect={callback} placeholder={"Seçin"} value={selected} name={name} style={{ width: 120 }}>
                    {selectAll ? <Option state={name} value={0}>Hamısı</Option> : null}
                    {options.map((w, i) => <Option state={name} key={new Date().getTime() + i} value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    render(h) {
        return (
            <div id={'addSection'}>
                <Row>
                    <Col span={8}>
                        <label>Mən:</label> <br />
                        <Radio.Group defaultValue="b" buttonStyle="solid">
                            <Radio.Button value="b">Satıram</Radio.Button>
                            <Radio.Button value="a">Kirayə Verirəm</Radio.Button>
                        </Radio.Group>
                    </Col>
                    <Col span={8}>
                    {this.selectGenerator("Kategoriya:", [], "Category", null)}
                    </Col>
                    <Col span={8}>col-8</Col>
                </Row>
            </div>
        )
    }
}

export default Add;
