import { Upload, Modal, Select, Checkbox, InputNumber, Collapse, Input } from 'antd';
const { Option } = Select;
const { Panel } = Collapse;
const { TextArea } = Input;

import { PlusOutlined } from '@ant-design/icons';


function getBase64(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}

class Utilities {

    constructor(th) {
        this.th = th;
    }

    inputGenerator = (title, placeholder, callback, input) => {
        return (
            <div className={'simpleInput subitem'}>
                <label>{title}</label>
                <Input placeholder={placeholder} value={input} onChange={callback} />
            </div>
        )
    }

    numberGenerator = (title, placeholder, callback, visibility) => {
        if (!visibility) return null;
        return (
            <div className={'numberInput subitem'}>
                <label className={'title'}>{title}</label>
                <InputNumber
                    className={'input'}
                    placeholder={placeholder}
                    formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                    parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    onBlur={callback}
                />
            </div>
        )
    }

    textAreaGeneretor = (title, placeholder, callback) => {
        return (
            <div className={'textAreaInput subitem'}>
                <label>{title}</label>
                <TextArea
                    placeholder={placeholder}
                    autoSize={{ minRows: 2, maxRows: 12 }}
                    onChange={callback}
                />
            </div>
        )
    }

    priceİnputGenerator = (title, minName, maxName, min, step, Callback, visibility) => {
        if (!visibility) return null
        return (
            <div className={'priceInput subitem'}>
                <div className={'item'}><label>{title}</label></div>
                <div className={'item'}>
                    <InputNumber
                        placeholder={"Min."}
                        onBlur={Callback}
                        onInput={Callback}
                        min={min} step={step} name={minName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                -
                <InputNumber
                        placeholder={"Max."}
                        onBlur={Callback}
                        onInput={Callback}
                        min={min} step={step} name={maxName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                </div>
            </div>
        )
    }

    selectGenerator = (title, options, name, selected, callback, sort, selectAll) => {
        if (!options) return null
        if (sort) {
            options.sort((a, b) => {
                let fa = a.name.toLowerCase()
                let fb = b.name.toLowerCase()

                if (fa < fb) return -1
                if (fa > fb) return 1
                return 0
            })
        }
        return (<div className={'selectInput subitem'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select onSelect={callback} placeholder={"Seçin"} value={selected} name={name} style={{ width: 120 }}>
                    {selectAll ? <Option state={name} value={0}>Hamısı</Option> : null}
                    {options.map((w, i) => <Option state={name} key={new Date().getTime() + i} value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    checkBoxGenerator = (title, callback, name) => {
        return (
            <div className={'checkInput subitem'}>
                <Checkbox state={name} onChange={callback}>{title}</Checkbox>
            </div>
        )
    }

    collapseAddition = () => {
        return (
           /* <Collapse>
                <Panel header="Əlavə" key="1">
                    {this.priceİnputGenerator("Mənzilin Mərtəbəsi:", "Min", "Max", 1, 1, this.minApartmantFloorCallback, this.maxApartmantFloorCallback, this.state.addition.apartmantFloor)}
                    {this.priceİnputGenerator("Binanın Mərtəbə Sayı:", "Min", "Max", 1, 1, this.minFloorAmountCallback, this.maxFloorAmountCallback, this.state.addition.floorAmount)}
                </Panel>
            </Collapse>*/ null
        )
    }

    radioGenerator = (label, values, defaultValue) => {
        return (
            <div className={"radioInput subitem"}>
                <label>{label}</label> <br />
                <Radio.Group defaultValue="b" buttonStyle="solid">
                    {}
                    
                </Radio.Group>
            </div>
        )
    }

    imageUploadGenerator = (fileList, previewVisible, previewTitle, previewImage) => {
        return (
            <>
                <Upload
                    action="http://192.168.1.107:5566/api/bina/image"
                    name={"image"}
                    multiple={true}
                    listType="picture-card"
                    fileList={fileList}
                    onPreview={this.handlePreview}
                    onChange={this.handleChange}
                >
                    {fileList.length >= 25 ? null : <div>
                        <PlusOutlined />
                        <div style={{ marginTop: 8 }}>Upload</div>
                    </div>}
                </Upload>
                <Modal
                    visible={previewVisible}
                    title={previewTitle}
                    footer={null}
                    onCancel={this.handleCancel}
                >
                    <img alt="example" style={{ width: '100%' }} src={previewImage} />
                </Modal>
            </>
        )
    }

    getImageState = {
        previewVisible: false,
        previewImage: '',
        previewTitle: '',
        fileList: []
    }

    handleCancel = () => this.th.setState({ previewVisible: false });

    handlePreview = async (file) => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        this.th.setState({
            previewImage: file.url || file.preview,
            previewVisible: true,
            previewTitle: file.name || file.url.substring(file.url.lastIndexOf('/') + 1),
        });
    };

    handleChange = async ({ fileList }) => {
        this.th.setState({ fileList: [...fileList] })
        this.th.setState({
            selected:
            {
                images: [...fileList].filter(w => w.response).map(w => w.response.fileName)
            }
        })
    };
}

export default Utilities;
