import {Save_Filter_Json} from '../actions/filterAction'

let store = {filterJson: null}

const filterSaver = (state = store, action)=>{
    switch (action.type) {
        case Save_Filter_Json:
            return Object.assign({},state, {
                ...action.payload
            })
        default:
            return {...state}
    }
}

export default filterSaver;
